import React from "react";
import ContactUsContent from '../components/contact-us';
import Banner from "../components/hero";

import { gql, useQuery } from '@apollo/client'

const query = gql`
query{  
  contactUsPage{
    hero{
      title
      subTitle
      background{
        url
      }
    }
  }
  addresses{
    id
    name
    openHours{
      header
      content{
        title
        description
      }
    }
    contactCenter{
      header
      content{
        title
        description
      }
    }
  }
}
`

const Contactus = () => {
  const { data, loading } = useQuery(query)
  if (loading)
    return <Loading/>
  return (
    <>
      <Banner hero={data.contactUsPage.hero} />
      <ContactUsContent />
    </>
  )

};

export default Contactus;
