import React, { useState } from 'react';
import { gql, useQuery } from '@apollo/client';
import styled from "styled-components";
import Articles from "../../../components/articles/normal/";
import Loading from "../../../components/loading/";
import Banner from "../../../components/hero/";


const SearchContainer = styled("div")`
    width:400px;
    margin:auto;
`;

const searchQuery = gql`
query ArticleQuery($q: String!) {
  articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
  articles(where:{_or:[{headerTitle_contains:$q },{headerSubTitle_contains:$q },{headerLongSubtitle_contains:$q }]}){
    id
    published_at
    featured

      headerTitle
      headerSubTitle
      thumbnail {
        url

      }
      mainMedia {
        url
      }
    	headerLongSubtitle
  }
}

`


export default function searchedArticles() {
  const { loading, error, data } = useQuery(searchQuery);
  if(loading)
  return <Loading/>
  console.log(data);

  return (
    <>
      <Banner hero={data.articlePage.hero} children={null} />
      <Articles unfeatured={data.unfeatured} />

    </>
  )
}
