import React from 'react';
import CorporateContent from '../components/corporate';
import Link from "next/link";
import styled from "styled-components";
import Banner from "../components/hero";
import Loading from "../components/loading/";
import { gql } from '@apollo/client';
import client from "../backend-client";
import DefaultErrorPage from 'next/error';

const query = gql`
query{  
  corporatePage{
    hero{
      title
      subTitle
      background{
        url
      }
    }
    infoSectionTitle
    infoSectionSubTitle
    infoSectionImage{
      url
    }
    safetySectionTitle
    safetySectionSubTitle
    architectureSectionTitle
    architectureSectionSubTitle
    expenseSectionSubTitle
    expenseTrackerSectionTitle
  }
  corporateInfos{
      title
    subTitle
    }
  corporateSafeties{
    title
    subTitle
    thumbnail{
      url
    }
  }
  corporateArchitectures{
    title
    subTitle
    thumbnail{
      url
    }
  }
  corporateExprenses{
    title
    subTitle
    thumbnail{
      url
    }
  }
}
`

const SloganButton = styled("button")`
background: #A02167;
box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.25);
border-radius: 5px;
padding: 5px 20px;
line-height: 19px;
text-align: center;
color: #ffffff;
border-color: transparent;
font-family: Open Sans;
font-style: normal;
font-weight: bold;
font-size: 14px;
line-height: 19px;
text-align: center;
`;

const children =
  <Link href="/signup/corporate">
    <SloganButton>Sign up for your company</SloganButton>
  </Link>
export async function getServerSideProps() {
  try{
  const { data, error } = await client.query({
    query: query
  });
  return {
    props: {
      data: data,
      error:error
    },
  };
} catch (e) {
  return {props:{error:true}};
}
}

const corporate = ({data, error}) => {
  if (error)
  return <DefaultErrorPage statusCode={404} />
  return (
    <>
      <Banner hero={data.corporatePage.hero} children={children} />
      <CorporateContent data={data} />;
    </>
  )
}


export default corporate;
