import React, { useState } from "react";
import styled from "styled-components";

const Card = styled("div")`
width:150px;
height:150px;
position:relative;
box-shadow: 1px 1px 10px rgba(0, 0, 0, 0.161);
margin-bottom:60px;
margin-top:20px;
cursor: pointer;
`;
const FileSelector = styled("input")`
opacity: 0;
position: absolute;
z-index: -1;

`;



const CenteredElement = styled("label")`
position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-family: Open Sans;
font-style: normal;
font-weight: bold;
font-size: 14px;
line-height: 19px;
color: #444444;
cursor: pointer;
`;

const ImagePreview = styled("img")`
width:150px;
height:150px;

`;



const Document = () => {

    const [imageUrl, setImage] = useState("");
    return (
        <>

            <Card >
                <CenteredElement htmlFor="upload-photo">
                    + Upload
                </CenteredElement>
                   {imageUrl!=""?<ImagePreview src={imageUrl}/>:null}
                    <FileSelector onChange={(event)=>setImage(URL.createObjectURL(event.target.files[0]))} type="file" name="photo" id="upload-photo" />


            </Card>


        </>
    );
}





export default Document;
