import React, { FunctionComponent } from "react";
import styled from "styled-components";
import { Input } from "./primary-input";

const CustomInput = styled(Input)`
border:2px solid #A02167;
border-left:none;
border-top-left-radius: 0px;
border-top-right-radius: 4px;
border-bottom-right-radius: 4px;
border-bottom-left-radius: 0px;
padding-left:50px;
&& {
    :focus {
        border:2px solid #A02167;
        border-left:none;
    }
`;

const Selector = styled("select")`
border: 2px solid #A02167;
border-right:none;
border-top-right-radius: 0px;
border-top-left-radius: 4px;
border-bottom-left-radius: 4px;
border-bottom-right-radius: 0px;
-webkit-appearance: none;
-moz-appearance: none;
background: transparent;
width:70px;
background-image: url("data:image/svg+xml;utf8,<svg fill='black' height='24' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><path d='M7 10l5 5 5-5z'/><path d='M0 0h24v24H0z' fill='none'/></svg>");
background-repeat: no-repeat;
background-position-x: 100%;
background-position-y: 50%;
padding: 12px 20px;
margin: 8px 0;
&& {
    :focus {
        border: 1px solid #A02167;
    }
`;

const CountryCodeContainer = styled("div")`
display:flex;
`;

const Code = styled("div")`
position: absolute;
  top: 50%;
  left:20px;
  transform: translate(-50%, -50%);
`;

const CodeContainer = styled("div")`
position: relative;
`;

const { countries } = require("../../../assets/Data/country-code.json");

let countriesList = countries.length > 0
    && countries.map((item, i) => {
        return (
            <option key={i} value={item.id}>{item.emoji}&emsp;{item.name}&emsp;{item.unicode}</option>
        )
});

const Container = styled("div")`
display:flex;
`;

const PhoneInput = ({ label, id, placeholder }) => {

    return (
        <>  <label >{label}</label>

            <Container>
                <CountryCodeContainer>
                <Selector>
                    {countriesList}
                </Selector>

                </CountryCodeContainer>
                <CodeContainer>
                <Code>+251</Code>
                </CodeContainer>
                <CustomInput placeholder={placeholder} id={id} />

            </Container>

        </>
    );
};

export { PhoneInput }
