import React from "react";
import styled from "styled-components";

const Card = styled("div")`
width:150px;
height:150px;
position:relative;
box-shadow: 1px 1px 10px rgba(0, 0, 0, 0.161);
margin-bottom:60px;
margin-top:20px;
cursor: pointer;
`;
const FileSelector = styled("input")`
opacity: 0;
position: absolute;
z-index: -1;

`;

const Element = styled("label")`
position:relative;
height:100%;
`;

const CenteredElement = styled("label")`
position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-family: Open Sans;
font-style: normal;
font-weight: bold;
font-size: 14px;
line-height: 19px;
color: #444444;
`;



const Document = () => {
    return (
        <>

            <Card >
                <CenteredElement htmlFor="upload-photo">
                    <Element> +Upload</Element>
                </CenteredElement>

                    <FileSelector type="file" name="photo" id="upload-photo" />


            </Card>


        </>
    );
}



export default Document;
