import React, { useState } from 'react';
import ArticlesContent from '../../components/articles';
import styled from "styled-components";
import { SecondaryInputs } from "../../components/form/inputs";
import Banner from "../../components/hero/";
import { gql, useQuery } from '@apollo/client';
import client from "../../backend-client";
import DefaultErrorPage from 'next/error';
import Articles from "../../components/articles/normal/";
import Loading from "../../components/loading/";


const SearchContainer = styled("div")`
    width:400px;
    margin:auto;
`;

const searchQuery = gql`
query ArticleQuery($q: String!) {
  articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
  articles(where:{_or:[{headerTitle_contains:$q },{headerSubTitle_contains:$q },{headerLongSubtitle_contains:$q }]}){
    id
    published_at
    featured

      headerTitle
      headerSubTitle
      thumbnail {
        url

      }
      mainMedia {
        url
      }
    	headerLongSubtitle
  }
}

`

const query = gql`
# Write your query or mutation here
query {
  	articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
    featured: articles(sort: "created_at" ,where: {featured:true}){
    id
    published_at
    featured
    headerTitle
    headerSubTitle
    thumbnail {
        url

    }
    mainMedia {
        url
    }
    headerLongSubtitle

  }
  unfeatured: articles(sort: "created_at" ,where: {featured:false}, limit:9){
    id
    published_at
    featured
    headerTitle
    headerSubTitle
    thumbnail {
        url

    }
    mainMedia {
        url
    }
    headerLongSubtitle

  }
}
`
export async function getServerSideProps() {
  try {
    const { data, error } = await client.query({
      query: query
    });
    return {
      props: {
        data: data
      },
    };
  } catch (e) {
    return { props: { error: true } };
  }
}

function searchedArticles() {
  const { loading, error, data } = useQuery(searchQuery);
  if (loading)
    return <Loading />

  return (
    <>
      <Banner hero={data.articlePage.hero} children={null} />
      <Articles unfeatured={data.unfeatured} />

    </>
  )
}

export default function articles({ data, error }) {
  const [isSearching, setSearching] = useState(false);

  const children = <SearchContainer>
    <SecondaryInputs
      id="location"
      placeholder="Search..."
      icon={require("../../assets/icons/search.svg")}
      onInput={(event) => {
        if (event.target.value == null || event.target.value == "") {
          setSearching(false);
        }
        else if (event.key == "Enter") {
          setSearching(true);
        }


      }}
    />
  </SearchContainer>

  if (error)
    return <DefaultErrorPage statusCode={404} />
  if (!isSearching)
    return (
      <>
        <Banner hero={data.articlePage.hero} children={children} />
        {
          isSearching ?
            <Articles unfeatured={data.unfeatured} /> :
            <ArticlesContent featured={data.featured} unfeatured={data.unfeatured} />
        }
      </>
    )
  if (isSearching)
    return searchedArticles();

}
