import React, { FunctionComponent } from "react";
import styled from "styled-components";

const Input = styled('input')`
width: 100%;
padding: 12px 0px;
margin-bottom: 40px;
display: inline-block;
border: 2px solid #ccc;
border-radius: 4px;
box-sizing: border-box;
margin-top:10px;
font-size:16px;
&& {
  :focus {
    outline: none;
    border: 2px solid #A02167;
  }
  padding-left: 25px;
  letter-spacing: 22px; 
  background-image: linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%);
  background-size: 20px 2px, 20px 2px,20px 2px,20px 2px,20px 2px,20px 2px;
    background-repeat:no-repeat;
    background-position:20px center, 50px center,80px center, 110px center, 140px center, 170px center;
`;
const LabelText = styled("label")`
  font-weight: normal;
  font-size: 16px;
  line-height: 16px;
  color: #444444;
`;

type InputProps = {
    id: string;
    label?: string;
    placeholder: string;
};

const DefaultOtpInput: FunctionComponent<InputProps> = ({
    label,
    id,
    placeholder
}) => {
    return (
        <>
            <LabelText>{label}</LabelText>
            <Input  onInput={(event)=>{
              alert(event.target.value.length);
              if(event.target.value.length>=6) return false;}}  type="number" maxLength="6" placeholder={placeholder} id={id} />
        </>
    );
};

export { DefaultOtpInput, Input }
