import React from "react";
import Head from "next/head";
import Header from "../components/header";
import Footer from "../components/footer";
import styled from "styled-components";
import { useRouter } from "next/router";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import GlobalStyle from "../theme/global/index";
import { ApolloProvider } from "@apollo/client";
import client from "../backend-client";

const FloatingButton = styled("div")`
	position:fixed;
	width:60px;
	height:60px;
	bottom:40px;
	right:38px;
	background-color:#A02167;
	border-radius:50px;
	text-align:center;
  z-index:100;

`;

const IconImage = styled("img")`
margin-top:18px;
`;

export default function MyApp({ Component, pageProps }) {
  const router = useRouter();
  function handleScroll(event) {
    let scrollTop = event.srcElement.body.scrollTop,
      itemTranslate = Math.min(0, scrollTop / 3 - 60);

    this.setState({
      transform: itemTranslate
    });
  }
  if (router.pathname.includes("/login")
    || router.pathname.includes("/signup")
    || router.pathname.includes("/booking")
  )
    return (
      <>
        <ApolloProvider client={client}>
          <Head>
            <link rel="preconnect" href="https://fonts.gstatic.com" />
            <link
              href="https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300;0,400;0,600;0,700;0,800;1,300;1,400;1,600;1,700;1,800&display=swap"
              rel="stylesheet"
            />
          </Head>
          <GlobalStyle />
          <Component {...pageProps} />
        </ApolloProvider>
      </>)
      ;
  return (
    <ApolloProvider client={client} onScroll={handleScroll}>
      <Head>
        <link rel="preconnect" href="https://fonts.gstatic.com" />
        <link
          href="https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300;0,400;0,600;0,700;0,800;1,300;1,400;1,600;1,700;1,800&display=swap"
          rel="stylesheet"
        />
      </Head>
      <GlobalStyle />
      <Header />
      <FloatingButton id="floating-button"><IconImage src={require("../assets/icons/call-center.svg")} /></FloatingButton>
      <Component {...pageProps} />
      <Footer />
    </ApolloProvider>
  );
}
