import React from "react";
import Head from "next/head";
import Header from "../components/header";
import Footer from "../components/footer";
import styled from "styled-components";
import { useRouter } from "next/router";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import GlobalStyle from "../theme/global/index";
import { ApolloProvider } from "@apollo/client";
import client from "../backend-client";

const FloatingButton = styled("div")`
`;

const IconImage = styled("img")`
margin-top:18px;
`;

export default function MyApp({ Component, pageProps }) {
  const router = useRouter();

  React.useEffect(() => {
    window.addEventListener("scroll", function () {
      if(window.pageYOffset<10){

        document.getElementById("floating-button").className = "hidden";
      }
      else{
        document.getElementById("floating-button").className = "";
      }

    }, false);

  }, []);



  if (router.pathname.includes("/login")
    || router.pathname.includes("/signup")
    || router.pathname.includes("/booking")
  )
    return (
      <>
        <ApolloProvider client={client}>
          <Head>
            <link rel="preconnect" href="https://fonts.gstatic.com" />
            <link
              href="https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300;0,400;0,600;0,700;0,800;1,300;1,400;1,600;1,700;1,800&display=swap"
              rel="stylesheet"
            />
          </Head>
          <GlobalStyle />
          <Component {...pageProps} />
        </ApolloProvider>
      </>)
      ;
  return (

    <ApolloProvider client={client}>
      <Head>
        <link rel="preconnect" href="https://fonts.gstatic.com" />
        <link
          href="https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300;0,400;0,600;0,700;0,800;1,300;1,400;1,600;1,700;1,800&display=swap"
          rel="stylesheet"
        />
      </Head>
      <GlobalStyle />
      <Header />
      <FloatingButton id="floating-button" className="hidden">
        <h1>This is the text</h1>
        <IconImage src={require("../assets/icons/call-center.svg")} /></FloatingButton>
      <Component {...pageProps} />
      <Footer />
    </ApolloProvider>

  );
}
