import React from 'react';
import Article from "../../components/articles/single";
import Banner from "../../components/hero/";
import { gql, useQuery } from '@apollo/client'


const articleQuery = gql`
# Write your query or mutation here
query {
    fetchArticle( $id:string ){
  	articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
    articles(sort: "created_at", where: {id:$id} ){
    id
    published_at
    featured
    header{
    	title
    	subTitle
    	thumbnail{
      	url
    	}

    }
      body {
      ... on ComponentTextMedia{
        caption
        media {
          url
        }
        meta
        __typename
      }
      ... on ComponentTextParagraph{
        content
        __typename
      }
    }
  }
    }
}
`


export default function articles() {
    const { data, loading } = useQuery(articleQuery, {
        variables: { id:"1" },
      })

    if (loading)
        return <Loading/>
    return (
        <>
            <Banner hero={data.articlePage.hero} children={null} />
            <Article />
        </>
    )

}
