import React from 'react';
import styled from "styled-components";
import Card from "./card";
import { Title, Block } from '../title';
import { DefaultSection } from "../../section";
import Slider from "../../slider";


const settings = {
  itemsToShow: 3,
  itemPadding: [25, 0],
  transitionMs: 700
}

const FeaturedContent = ({ featured }) => {
  function myArrow({ type, onClick, isEdge }) {

    return (
      type === consts.PREV ?
        <button onClick={onClick} className="rec rec-arrow rec rec-arrow-left" disabled={isEdge} type="button"></button>
        :
        <button onClick={onClick} className="rec rec-arrow rec rec-arrow-right" disabled={isEdge} type="button"></button>
    )
  }
  return (
    <>
      <DefaultSection>
        <Block>
          <Title>Featured</Title>
        </Block>
        <Slider settings={settings}

          content={featured.map((article) => (
            <Card key={article.id} article={article} />
          ))}
        />

      </DefaultSection>
    </>
  );


}


export default FeaturedContent;
