import React from 'react';
import AboutUsContent from '../components/about-us';
import Banner from "../components/hero";
import Loading from "../components/loading/";

import { gql, useQuery } from '@apollo/client'

const query = gql`
query{
  aboutUsPage{
    id
    hero{
      title
      subTitle
      background{
        url
      }
    }

    aboutUsSectionTitle
    aboutUsSectionDescription{
      content
    }
    aboutUsSectionPrimaryImage{
      url
    }
    aboutUsSectionSecondaryImage{
      url
    }
    whyUsSectionTitle
    whyUsSectionSubTitle
    whyAsMainImage{
      url
    }
    ourPartnersSectionTitle
    ourPartnersSectionSubTitle
    ourPartnersSectionDescription
    ourTeamSectionTitle
    ourTeamSectionSubTitle
  }

  ourValueStatements{
    id
    title
    description
    thumbnail{
      url
    }
  }

  whyTaxiyes{
    id
    title
    description
  }

  ourPartners{
    id
    description
    thumbnail{
      url
    }
  }

  ourTeams{
    id
    name
    title
    profile{
      url
    }
  }
  ourTeamGroups{
    id
    team
    description
    image{
      url
    }
  }


}
`

const aboutus = () => {
    const { data, loading } = useQuery(query)
    if (loading)
        return <Loading/>
    return (
        <>
            <Banner hero={data.aboutUsPage.hero} children={null}/>
            <AboutUsContent data={data}/>
        </>
    )
}

export default aboutus;
