import React from 'react';
import Article from "../../components/articles/single";
import { gql, useQuery } from '@apollo/client'


const SearchContainer = styled("div")`
    width:400px;
    margin:auto;
`;

const children = <SearchContainer>
    <SecondaryInputs
        id="location"
        placeholder="Search..."
        icon={require("../../assets/icons/search.svg")}
    />
</SearchContainer>



const articleQuery = gql`
# Write your query or mutation here
query {
  	articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
    articles(sort: "created_at" ){
    id
    published_at
    featured
    header{
    	title
    	subTitle
    	thumbnail{
      	url
    	}

    }
      body {
      ... on ComponentTextMedia{
        caption
        media {
          url
        }
        meta
        __typename
      }
      ... on ComponentTextParagraph{
        content
        __typename
      }
    }
  }
}
`


export default function articles() {
    const { data, loading } = useQuery(articleQuery)

    if (loading)
        return <Loading/>
    return (
        <>
            <Banner hero={data.articlePage.hero} children={children}/>
            <Article />
        </>
    )

}
