import React from 'react';
import Article from "../../components/articles/single";
import Banner from "../../components/hero/";
import { gql, useQuery } from '@apollo/client';
import { useRouter } from "next/router";


const articleQuery = gql`
query ArticleQuery( $id: ID! ){
    articlePage{
         hero{
             title
             subTitle
             background{
                 url
             }

         }
     }
      article(id:$id ){
      id
      published_at
      featured
      header{
          title
          subTitle
          thumbnail{
            url
          }
        mainMedia{
          url
        }

      }
        body {
        ... on ComponentTextMedia{
          caption
          media {
            url
          }
          meta
          __typename
        }
        ... on ComponentTextQuote{
        content
        __typename
       }
        ... on ComponentTextParagraph{
          content
          __typename
        }
      }
    }

  }
`


export default function articles() {
    const router = useRouter();
    const article_id = router.query.id;


    const { data, loading } = useQuery(articleQuery, {
        variables: { id: article_id },
      })

    if (loading)
        return <Loading/>
    return (
        <>
            <Banner hero={data.articlePage.hero} children={null} />
            <Article article={data.article}/>
        </>
    )

}
