import React from 'react';
import LandingContent from '../components/landing';
import Banner from "../components/hero/landing-hero";
import { gql } from '@apollo/client';
import client from "../backend-client";
import Loading from "../components/loading/";
import { useRouter } from 'next/router'

const homePageQuery = gql`
# Write your query or mutation here
query {
  homePage {
    hero{
      title
      subTitle
      background{
        url
      }
    }
    serviceSectionTitle
    serviceSectionSubTitle
    testimonySectionTitle
    testimonySectionSubTitle
    fleetSectionTitle
    fleetSectionSubTitle
    articleSectionTitle
    articleSectionSubTitle

  }
  services(sort: "created_at" ){
    id
    name
    shortDescription{
      content
    }
    longDescription{
      content
    }
    thumbnail{
      url
    }

  }

    articles(sort: "created_at" ){
    id
    published_at
    featured
    header{
    	title
    	subTitle
    	thumbnail{
      	url
    	}

    }
  }
  testimonies(sort: "created_at" ){
  	id
    profileImage{
      url
    }
  	fullName
    position
    testimony
    rating
  }
    fleets(sort: "created_at" ){
  	id
    name
    description
    speed
  	image{
      url
    }
  }
}
`

export async function getServerSideProps() {
  const { data, loading } = await client.query({
    query: homePageQuery
  });
  if (!data) {
    return {
      notFound: true,
    }
  }

  return {
    props: {
      data: data,
      loading:loading
    },
 };
}


export default function index({data, loading}) {

  const router = useRouter();
    const { isFallback } = router
    if (isFallback) {
        return <Loading />
    }

    return(
    <>
      <Banner hero={data.homePage.hero} />
      <LandingContent data={data} />;
    </>
    )
}
