import React from "react";
import styled from "styled-components";
import { useRouter } from "next/router";
import { SecondaryInputs } from "../form/inputs";
import Link from "next/link";


const SlogganWrapper = styled("div")`
display; flex;
flex-direction: column;
justify-content: center;
align-items: center;
text-align: center;
align-self: center;
margin:auto;

`;

const Slogan = styled("h1")`
  font-weight: bold;
  font-size: 36px;
  line-height: 49px;
  color: #ffffff;
`;


const CenteredSlogan = styled(Slogan)`
  text-align: center;
  padding-bottom: 10px;
`;

const Description = styled("p")`
  font-weight: 600;
  font-size: 18px;
  line-height: 25px;
  text-align: center;
  color: #ffffff;
  padding-bottom: 30px;
`;

const Breadcrump = styled("h2")`
  margin: auto;
  display: block;
  max-width: 250px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  font-style: normal;
  font-weight: 600;
  font-size: 16px;
  line-height: 22px;
  text-align: center;
  color: #A02167;
  padding-bottom: 10px;
  &::firstline {
    text-transform: uppercase;
  }
`;


const SloganButton = styled("button")`
background: #A02167;
box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.25);
border-radius: 5px;
padding: 5px 20px;
line-height: 19px;
text-align: center;
color: #ffffff;
border-color: transparent;
font-family: Open Sans;
font-style: normal;
font-weight: bold;
font-size: 14px;
line-height: 19px;
/* identical to box height */

text-align: center;

`;

const Hero = ({ hero }) => {
  const router = useRouter();
  let slogan= hero.shortDesctiption.content;
  let backgroundUrl = `${require("../../assets/images/heros/services.jpg")}`;
  let description =
    "Lorem ipsum dolor sit amet consectetur adipiscing elit sodales primis, mollis viverra conubia ligula inceptos laoreet.";
  let queryText = "";

  const HeroWrapper = styled("div")`
    display: flex;
    background: linear-gradient(0deg, rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)),
      url(${backgroundUrl});
    background-repeat: no-repeat;
    background-size: cover;
    height:550px;
    width:100%;
  `;

  const Container = styled("div")`

  padding-top:95px;
  margin:auto;
`;

  const SearchContainer = styled("div")`
width:400px;
margin:auto;
`;


  let routes = router.asPath.split("/");
  routes.shift();

  const navigatedLink = routes[0].replace(/\\|\//g, "");

  return (
    <>

        <HeroWrapper >
          <Container>
            <SlogganWrapper>
              <Breadcrump>
                {`Home / 
                  ${navigatedLink.charAt(0).toUpperCase() + navigatedLink.slice(1)}


                  ${queryText != "" ? ` / 
                  ${queryText.charAt(0).toUpperCase() + queryText.slice(1)}
                  `: ``}`}
              </Breadcrump>
              <CenteredSlogan>{slogan}</CenteredSlogan>
              <Description>{description}</Description>
              {router.pathname === "/corporate" ? (
                <Link href="/signup/corporate">
                  <SloganButton>Sign up for your company</SloganButton>
                </Link>
              ) : router.pathname === "/articles" ? (
                <SearchContainer>
                  <SecondaryInputs
                    id="location"
                    placeholder="Search..."
                    icon={require("../../assets/icons/search.svg")}
                  />
                </SearchContainer>
              ) : router.pathname === "/become-driver" ? (
                <Link href="/signup/driver">
                  <SloganButton>Get started</SloganButton>
                </Link>
              ) : null}
            </SlogganWrapper>
          </Container>
        </HeroWrapper>

    </>
  );
};

export default Hero;
