import React from 'react';
import styled from "styled-components";
import ContactUsForm from "./form/form";
import Infos from "./form/info";
import { DefaultSection, SecondarySection, SectionTitle, CenteredText,SectionHeaderContainer  } from "../section";
import { Button } from "../form/buttons/primary-button";
import MapViewer from "./map-view/map";

const FormContainer = styled("div")`
    display: flex;
    justify-content:space-between;
    padding: 0px 60px;
`;
const MapContainer = styled("div")`
position:relative;

`;
const MapInfoContainer = styled("div")`
    text-align: center;
    justify-content:center;
    position:absolute;
    right:0px;
    top:0px;
    margin:auto;
    background-color:#fff;
    width:400px;
    height: -webkit-fill-available;
`;
const SectionTitleOnStart = styled(SectionTitle)`
    text-align: start;
    width: 100%;
`;

const CenteredTextOnStart = styled(CenteredText)`
    text-align: start;
    width: 100%;
    max-width: inherit;
`;

const info = {
    id: "1",
    header: "Let's talk...",
    headersubTitle: "Ask us anything or just say hi...",
    content: [{
        title: "Open Hours",
        details: [
            "Monday - Friday",
            "8 am - 5 pm",
            "Saturday",
            "8 am - 12 pm"
        ],
    },
    {
        title: "Contact Support",
        details: [
            "info@elnetech.com",
            "6055 - Free call 24/7"
        ],
    }
    ]

};



const InfosContainer = styled("div")`
padding-right:150px;

`;




const ContactUSContent = () => {
    return (
        <>
            <DefaultSection>
                <FormContainer>
                        <InfosContainer>
                        <Infos key={info.id} info={info} />
                        </InfosContainer>


                    <ContactUsForm/>
                </FormContainer>
            </DefaultSection>



        </>
    );
}

export default ContactUSContent;
