import React from "react";
import styled from "styled-components";

const Card = styled("div")`
width:150px;
height:150px;
position:relative;
box-shadow: 1px 1px 10px rgba(0, 0, 0, 0.161);
margin-bottom:60px;
margin-top:20px;
`;
const FileSelector = styled("input")`
display:none;
`;



const CenteredElement = styled("div")`
position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-family: Open Sans;
font-style: normal;
font-weight: bold;
font-size: 14px;
line-height: 19px;
color: #444444;
`;



const Document = () => {
    return (
        <>

            <Card>
                <CenteredElement>
                    + Upload
                        <FileSelector type="file" name="filename" accept="image/gif, image/jpeg, image/png" style="display:none"/>
                </CenteredElement>
            </Card>


        </>
    );
}



export default Document;
