import React from 'react';
import styled from "styled-components";

const Image = styled("img")`

`;


const Info = styled("div")`
  padding-right:10px;
`;

const AuthorText = styled("span")`
  font-weight: normal;
  font-size: 12px;
  line-height: 150%;
  color: #979797;
  margin-left: 5px;
`;

const ArticleInfoWrapper = styled("div")`
  display: flex;
  padding-bottom: 10px;

`;

const ArticleTitle = styled("h1")`
  font-style: normal;
  font-weight: 600;
  font-size: 20px;
  line-height: 30px;
  color: #444444;
  padding: 0px 0px 10px 0px;
`;


const ArticleHeader = ({header}) => {
    return (
        <>
            <ArticleInfoWrapper>
                <Info>
                    <Image
                        src={require("../../../../assets/icons/user/vector.svg")}
                    />
                    <AuthorText>{header.author}</AuthorText>
                </Info>
                <Info>
                    <Image
                        src={require("../../../../assets/icons/user/clock.svg")}
                    />
                    <AuthorText>{header.date}</AuthorText>
                </Info>
            </ArticleInfoWrapper>
            <ArticleTitle>{header.title}</ArticleTitle>

        </>
    );
}

export default ArticleHeader;
