import React from 'react';
import styled from "styled-components";
import ContactUsForm from "./form";
import Infos from "./info";
import { DefaultSection, SecondarySection, SectionTitle, CenteredText,SectionHeaderContainer, SectionContentContainer  } from "../section";
import { Button } from "../form/inputs";
import MapViewer from "./map";
import Socials from "./socials";

const FormContainer = styled("div")`
    display: flex;
    justify-content:space-between;

`;
const MapContainer = styled("div")`
position:relative;

`;
const MapInfoContainer = styled("div")`
    text-align: center;
    position:absolute;
    right:0px;
    top:0px;
    background-color:#fff;
`;
const SectionTitleOnStart = styled(SectionTitle)`
    text-align: start;
    width: 100%;
`;

const CenteredTextOnStart = styled(CenteredText)`
    text-align: start;
    width: 100%;
    max-width: inherit;
`;

const info = {
    id: "1",
    header: "Let's talk...",
    headersubTitle: "Ask us anything or just say hi...",
    content: [{
        title: "Open Hours",
        details: [
            "Monday - Friday",
            "8 am - 5 pm",
            "Saturday",
            "8 am - 12 pm"
        ],
    },
    {
        title: "Contact Support",
        details: [
            "info@elnetech.com",
            "6055 - Free call 24/7"
        ],
    }
    ]

};

const mapinfo = {
    id: "2",
    secondaryheader: "Taxiye Addis Ababa",
    content: [{
        title: "Open Hours",
        details: [
            "Monday - Friday",
            "8 am - 5 pm",
            "Saturday",
            "8 am - 12 pm"
        ],
    },
    {
        title: "Contact Support",
        details: [
            "info@elnetech.com",
            "6055 - Free call 24/7"
        ],
    }
    ]

};


const ContactUSContent = () => {
    return (
        <>
            <DefaultSection>
                <SectionContentContainer>
                <FormContainer>
                    <div>
                        <Infos key={info.id} info={info} />
                        <Socials />
                    </div>
                    <ContactUsForm></ContactUsForm>
                </FormContainer>
                </SectionContentContainer>
            </DefaultSection>
            <SecondarySection>
            <SectionHeaderContainer>
                <SectionTitleOnStart> Our Offices </SectionTitleOnStart>
                <CenteredTextOnStart> Our doors are open to all. Come and visit us! </CenteredTextOnStart>
                </SectionHeaderContainer>
                </SecondarySection>
                <MapContainer>
                    <MapViewer />
                    <MapInfoContainer>
                        <div>
                        <Infos key={mapinfo.id} info={mapinfo} />
                        <Button>Get directions</Button>
                        </div>
                    </MapInfoContainer>

                </MapContainer>
        </>
    );
}

export default ContactUSContent;
