import React from 'react';
import LandingContent from '../components/landing';
import Banner from "../components/hero/landing-hero";
import Loading from "../components/loading/";

import { gql, useQuery } from '@apollo/client';
import client from "../backend-client";

const homePageQuery = gql`
# Write your query or mutation here
query {
  homePage {
    hero{
      title
      subTitle
      background{
        url
      }
    }
    serviceSectionTitle
    serviceSectionSubTitle
    testimonySectionTitle
    testimonySectionSubTitle
    fleetSectionTitle
    fleetSectionSubTitle
    articleSectionTitle
    articleSectionSubTitle

  }
  services(sort: "created_at" ){
    id
    name
    shortDescription{
      content
    }
    longDescription{
      content
    }
    thumbnail{
      url
    }

  }

    articles(sort: "created_at" ){
    id
    published_at
    featured
    header{
    	title
    	subTitle
    	thumbnail{
      	url
    	}

    }
  }
  testimonies(sort: "created_at" ){
  	id
    profileImage{
      url
    }
  	fullName
    position
    testimony
    rating
  }
    fleets(sort: "created_at" ){
  	id
    name
    description
    speed
  	image{
      url
    }
  }
}
`

export async function getStaticProps() {
  const { data } = await client.query({
    query: gql`
      query Countries {
        countries {
          code
          name
          emoji
        }
      }
    `,
  });

  return {
    props: {
      countries: data.countries.slice(0, 4),
    },
 };
}


export default function index() {
  const { data, loading } = useQuery(homePageQuery)

  if (loading)
    return <Loading/>
    return(
    <>
      <Banner hero={data.homePage.hero} />
      <LandingContent data={data} />;
    </>
    )
}
