  let hero = {
    background: articlePage.hero.background,
    title: `${article.headerTitle}`,
    subTitle: "",
    text: `${article.headeeTitle}`,
  };

  return (
    <>
      <Banner hero={hero} children={null} />
      <Article article={article} featured={populars} />
    </>
  )

}
