import React from 'react';
import styled from "styled-components";

const Container = styled("div")`
text-align: center;
padding-top:30px;
`;
const Figure = styled("figure")`
display: inline-block;
margin:0px;
`;
const Caption = styled("figcaption")`
width: 540px;
font-family: Open Sans;
font-style: italic;
font-weight: normal;
font-size: 14px;
line-height: 21px;
color: #2A2A2A;
text-align: start;
padding:10px 20px 0px 20px;
&:before {
    content: "";
    height: 100%;
    width: 0px;
    background-color: #A02167;
    position: relative;
    border-left: 2px solid #A02167;
  }
`;

const Image = styled("img")`
width: 560px;
/*height: 319px;*/
`;


const Quote = ({ media }) => {
    return (
        <>
            <Container>
                <Figure>
                    <Image src={media.url} />
                    <Caption>{media.caption}</Caption>
                </Figure>
            </Container>
        </>
    );
}

export default Quote;
