import React from 'react';
import styled from 'styled-components';
import Rating from './rating';

const Card = styled('div')`
width:500px;
align-items: center;
background: #FFFFFF;
box-shadow: 0px 4px 25px rgba(0, 0, 0, 0.25);
border-radius: 10px;
padding: 30px 40px;
z-index: 1;
margin:10px;

`;

const Avatar = styled("img")`
height: 85px;
width: 85px;
position: static;
flex: none;
order: 0;
flex-grow: 0;
margin: 15px auto;
display:block;
justify-content:center;

`;

const CardTitle = styled('h3')`
font-weight: 600;
font-size: 20px;
line-height: 27px;
text-align: center;
margin: 5px 0px;
`;

const CardSubTitle = styled('h5')`
font-size: 14px;
line-height: 19px;
text-align: center;
color: #444444;
margin: 5px 0px;
`;

const CardText = styled('p')`
font-size: 16px;
line-height: 22px;
text-align: center;
color: #444444;
`;

const TestimonyCard = ({testimony}) => {
    return (
        <Card>
            <Avatar src={"http://localhost:1337"+testimony.profileImage.url} />
            <CardTitle>{testimony.fullName}</CardTitle>
            <CardSubTitle>{testimony.position}</CardSubTitle>
            <CardText>{testimony.testimony}</CardText>
            <Rating key={testimony.id} rate={testimony.rating}  />
        </Card>
    );
}

export default TestimonyCard;
