import React from 'react';
import styled from "styled-components";
import colors from '../../theme/main/colors';

const OfficeDetail = styled("div")`  
    width:400px;
`;


const HeaderTitle = styled('h3')`
font-family: Open Sans;
font-style: normal;
font-weight: 600;
font-size: 32px;
color:${colors.primary};
margin: 0px 0px;
`;

const HeaderSubTitle = styled('p')`
font-family: Open Sans;
font-style: normal;
font-weight: normal;
font-size: 18px;
line-height: 25px;
margin: 0.5em 0px;
color: #444444;
`;
const ContentTitle = styled('h5')`
font-family: Open Sans;
font-style: normal;
font-weight: 600;
font-size: 16px;
`;


const ContentDetailText = styled('p')`
font-size: 16px;
line-height: 22px;
margin: 0.3em 0px;
color: #444444;
`;
const ContentContainer = styled('div')`
width:114px
`;


const info = {
    header: "Let's talk...",
    headersubTitle: "Ask us anything or just say hi...",
    content: [{
        title: "Open Hours",
        detail: "Monday - Friday 8 am - 5 pm Saturday 8 am - 12 pm",
    },
    {
        title: "Contact Support",
        detail: "info@elnetech.com 6055 - Free call 24/7",
    }
    ]

};

const Info = () => {
    return (
        <div>
            <HeaderTitle>
                {info.header}
            </HeaderTitle>
            <HeaderSubTitle>{info.headersubTitle}</HeaderSubTitle>
            {info.content.map((content) => (
           <div>
           <ContentTitle>{content.title}</ContentTitle>
           <ContentTitle>{content.detail}</ContentTitle>
           </div>

         ))}

        </div>
    );
}

export default Info;
