import React, { useState } from 'react';
import ArticlesContent from '../../components/articles';
import styled from "styled-components";
import { SecondaryInputs } from "../../components/form/inputs";
import Banner from "../../components/hero/";
import DefaultErrorPage from 'next/error';
import Articles from "../../components/articles/normal/";
import Loading from "../../components/loading/";
import { useQuery } from "react-query";
import { Button } from "../../components/form/buttons/primary-button";

const CustomButton = styled(Button)`
  padding: 5px 20px;
`;
const GraySection = styled("div")`
background: #f9f9f9;
margin-top: -60px;
padding-bottom:60px;
`;


const Container = styled("div")`

`;
const SearchContainer = styled("div")`
    width:400px;
    margin:auto;
`;
const NoContent = styled("div")`
font-family: Open Sans;
font-style: normal;
font-weight: 600;
font-size: 20px;
line-height: 27px;
color: #000000;
text-align:center;
`;

export async function getServerSideProps() {
  const { NEXT_PUBLIC_HOST } = process.env;
  const resArticlePage = await fetch(`${NEXT_PUBLIC_HOST}/article-page`);
  const articlePageData = await resArticlePage.json();
  const featuredRes = await fetch(`${NEXT_PUBLIC_HOST}/articles/?featured=true`);
  const featuredArticlesData = await featuredRes.json();
  const unfeaturedRes = await fetch(`${NEXT_PUBLIC_HOST}/articles/?featured=false`);
  const unfeaturedArticlesData = await unfeaturedRes.json();
  return {
    props: {
      articlePage: articlePageData,
      featured: featuredArticlesData,
      unfeatured: unfeaturedArticlesData,
      articles:[]
    },
  };
}
const getSuggestion = async (key) => {
  const { NEXT_PUBLIC_HOST } = process.env;
  const q = key.queryKey[1].content;
  if (q) {
    const res = await fetch(`${NEXT_PUBLIC_HOST}/articles?headerTitle_contains=${q}`);
    return res.json();
  }

  return [];
}
const getSearch = async (key) => {
  const { NEXT_PUBLIC_HOST } = process.env;
  const q = key.queryKey[1].content;
  if (q) {
    const res = await fetch(`${NEXT_PUBLIC_HOST}/articles?headerTitle_contains=${q}`);
    return res.json();
  }
  const res = await fetch(`${NEXT_PUBLIC_HOST}/articles`);
  return res.json();
}

export default function articles({ articlePage, featured, unfeatured, articles }) {
  const [searchText, setSearching] = useState("");
  const [searchText2, setSearching2] = useState("");
  const { data, status } = useQuery(['articles', { content: searchText }], getSearch, { initialData: articles });
  const { data: searchData, status:searchStatus } = useQuery(['articles', { content: searchText2 }], getSuggestion, { initialData: articles });


  const children = <SearchContainer key="keykjddfdfekjr">
    <SecondaryInputs
      id="location"
      placeholder="Search..."
      icon={require("../../assets/icons/search.svg")}
      lists={searchData}
      value={searchText2}
      onChange={(event) => {
          // setSearching2(event.target.value);
      }}
      onInput={(event) => {
        if (event.target.value == null || event.target.value == "") {
          setSearching("");
        }
        else if(event.key=="Enter"){
          setSearching(event.target.value);
        }


      }}
    />
  </SearchContainer>
  if (searchText != "" && status == "error")
    return <DefaultErrorPage statusCode={404} />
  else if (searchText != "" && status === "loading")
    return <Loading />

  return (
    <>
      <Banner hero={articlePage.hero} children={children} />
      <Container>
        {
          searchText != "" ?

            <Articles articles={data} />
            :
            <ArticlesContent featured={featured} unfeatured={unfeatured} />

        }
        <GraySection>
          {

            Array.isArray(data) && !data.length ?
              <NoContent>No content</NoContent> :
              Array.isArray(data) && data.length < 12 ?
                null :
                <CustomButton >Load More</CustomButton>


          }
        </GraySection>
      </Container>
    </>
  )

}
