import React, {FunctionComponent} from "react";
import styled from "styled-components";

const DropDown = styled("select")`
  background: #fff;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.25);
  border-radius: 5px;
  padding: 5px 20px;

`;
const LabelText = styled("label")`
  font-weight: normal;
  font-size: 16px;
  line-height: 16px;
  color: #444444;
  width: 100%;
`;

type InputProps = {
  id: string;
  label?: string;
  placeholder: string;
  type?: string;
};

const GenderDropDown: FunctionComponent<InputProps> = ({
    label,
    id,
    placeholder
  }) => {
    return (
      <>
      <LabelText forHtml={id}>{label}</LabelText>
      <div>
     <DropDown placeholder={placeholder} id={id}>
      <option> Male</option>
      <option> Female</option>
      </DropDown>
      </div>
     </>
    );
  };

export {GenderDropDown}
