import React, { Children } from 'react';
import ArticlesContent from '../../components/articles';
import styled from "styled-components";
import { SecondaryInputs } from "../../components/form/inputs";
import Banner from "../../components/hero/";

import { gql, useQuery } from '@apollo/client'


const SearchContainer = styled("div")`
    width:400px;
    margin:auto;
`;

const children = <SearchContainer>
    <SecondaryInputs
        id="location"
        placeholder="Search..."
        icon={require("../../assets/icons/search.svg")}
    />
</SearchContainer>



const articleQuery = gql`
# Write your query or mutation here
query {
  	articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
    articles(sort: "created_at" ,where: {featured:true}){
    id
    published_at
    featured
    header{
    	title
    	subTitle
        longSubTitle
    	thumbnail{
      	url
    	}
        mainMedia{
            url
        }

    }

  }
}
`


export default function articles() {
    const { data, loading } = useQuery(articleQuery)

    if (loading)
        return <Loading/>
    return (
        <>
            <Banner hero={data.articlePage.hero} children={children}/>
            <ArticlesContent featured={data.articles} unfeatured={data.articles}/>
        </>
    )

}
