import React from "react";
import { CenteredText, GraySection, SectionTitle } from "../section";
import styled from "styled-components";

const CardContainer = styled("div")`
display: flex;
flex-direction: row;
justify-content: center;
`;

const SubTitle = styled("h3")`
font-family: Open Sans;
font-style: normal;
font-weight: normal;
font-size: 16px;
line-height: 22px;

color: #363636;
`;

const Title = styled("p")`
font-family: Open Sans;
font-style: normal;
font-weight: 600;
font-size: 20px;
line-height: 27px;

color: #363636;
`;

const StringContainer = styled("div")`
max-width:410px;
`;

const ElementContainer = styled("div")`

`;

const contents = [{
    number: "1",
    title: "Greater Contro",
    subtitle: 'Set your own trip terms, create cost centers, and control budget allocation.'
},
{
    number: "2",
    title: "Greater Contro",
    subtitle: 'Set your own trip terms, create cost centers, and control budget allocation.'
},
{
    number: "3",
    title: "Greater Contro",
    subtitle: 'Set your own trip terms, create cost centers, and control budget allocation.'
}
];


const Requirements = () => {
    return (
        <>
            <GraySection>
                <SectionTitle>What are you waiting for? Start today.</SectionTitle>
                <CenteredText>Get Started</CenteredText>
                <CardContainer>
                    <div>
                    {contents.map((content) => (
                        <CardContainer>
                        <img src={contents.img} />
                        <ElementContainer>

                            <StringContainer>
                                <Title>{content.title}</Title>
                                <SubTitle>{content.subtitle}</SubTitle>
                            </StringContainer>

                         </ElementContainer>
                        </CardContainer>

                       ))}
                    </div>
                    <img src={require("../../assets/icons/requirements.svg")} />
                </CardContainer>
            </GraySection>
        </>
    );
};

export default Requirements;
