import React, { useState } from 'react';
import ArticlesContent from '../../components/articles';
import styled from "styled-components";
import { SecondaryInputs } from "../../components/form/inputs";
import Banner from "../../components/hero/";
import { gql } from '@apollo/client';
import client from "../../backend-client";
import DefaultErrorPage from 'next/error';
import Articles from "../../components/articles/normal/";
import Loading from "../../components/loading/";
import { useQuery } from "react-query";


const SearchContainer = styled("div")`
    width:400px;
    margin:auto;
`;

const searchQuery = gql`
query ArticleQuery($q: String!) {
  articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
  articles(where:{_or:[{headerTitle_contains:$q },{headerSubTitle_contains:$q },{headerLongSubtitle_contains:$q }]}){
    id
    published_at
    featured

      headerTitle
      headerSubTitle
      thumbnail {
        url

      }
      mainMedia {
        url
      }
    	headerLongSubtitle
  }
}

`

const query = gql`
# Write your query or mutation here
query {
  	articlePage{
      hero{
        title
        subTitle
        background{
          url
        }
      }
    }
    featured: articles(sort: "created_at" ,where: {featured:true}){
    id
    published_at
    featured
    headerTitle
    headerSubTitle
    thumbnail {
        url

    }
    mainMedia {
        url
    }
    headerLongSubtitle

  }
  unfeatured: articles(sort: "created_at" ,where: {featured:false}, limit:9){
    id
    published_at
    featured
    headerTitle
    headerSubTitle
    thumbnail {
        url

    }
    mainMedia {
        url
    }
    headerLongSubtitle

  }
}
`
export async function getServerSideProps() {
  const { NEXT_PUBLIC_HOST } = process.env;

  const res = await fetch(`${NEXT_PUBLIC_HOST}/articles`);
  const articlesData = await res.json();
  const resArticlePage = await fetch(`${NEXT_PUBLIC_HOST}/article-page`);
  const articlePageData = await resArticlePage.json();
  const featuredRes = await fetch(`${NEXT_PUBLIC_HOST}/articles`);
  const featuredArticlesData = await featuredRes.json();
  const unfeaturedRes = await fetch(`${NEXT_PUBLIC_HOST}/articles`);
  const unfeaturedArticlesData = await unfeaturedRes.json();

  return {
    props: {
      articlePage: articlePageData,
      featured: featuredArticlesData,
      unfeatured: unfeaturedArticlesData
    },
  };

}
const getSearch = async (key) => {
  const { NEXT_PUBLIC_HOST } = process.env;
  const q = key.queryKey[1].content;
  if(q){
  const res = await fetch(`${NEXT_PUBLIC_HOST}/articles?headerTitle_contains=${q}`);
  return res.json();
  }
  const res = await fetch(`${NEXT_PUBLIC_HOST}/articles`);
  return res.json();
}

export default function articles({ articlePage, featured, unfeatured, articles, error }) {
  const [searchText, setSearching] = useState("");
  const { data, status } = useQuery(['articles', { content: searchText }], { initialData: articles });

  const children = <SearchContainer>
    <SecondaryInputs
      id="location"
      placeholder="Search..."
      icon={require("../../assets/icons/search.svg")}
      onInput={(event) => {
        if (event.target.value == null || event.target.value == "") {
          setSearching("");
        }
        else if (event.key == "Enter") {
          setSearching(event.target.value);
        }


      }}
    />
  </SearchContainer>
  if (searchText!=""&&(status==="loading" || status=="error"))
    return <DefaultErrorPage statusCode={404} />

  return (
    <>
      <Banner hero={articlePage.hero} children={children} />
      {
        searchText!="" ?
          <Articles unfeatured={data} />
         :
          <ArticlesContent featured={featured} unfeatured={unfeatured} />
      }
    </>
  )

}
