import React from 'react';
import Article from "../../components/articles/single";
import Banner from "../../components/hero/";
import { gql, useQuery } from '@apollo/client';
import { useRouter } from "next/router";
import Loading from "../../components/loading/";


const articleQuery = gql`
query ArticleQuery( $id: ID! ){
    articlePage{
         hero{
             title
             subTitle
             background{
                 url
             }

         }
     }
     featured: articles(sort: "created_at" ,where: {featured:true}){
    id
    published_at
    featured
    header{
    	title
    	subTitle
        longSubTitle
    	thumbnail{
      	url
    	}
        mainMedia{
            url
        }

    }

  }
      article(id:$id ){
      id
      published_at
      featured
      header{
          title
          subTitle
          longSubTitle
          thumbnail{
            url
          }
        mainMedia{
          url
        }

      }
        body {
        ... on ComponentTextMedia{
          caption
          media {
            url
          }
          meta
          __typename
        }
        ... on ComponentTextQuote{
        content
        __typename
       }
        ... on ComponentTextParagraph{
          content
          __typename
        }
      }
    }

  }
`


export default function articles() {
  const router = useRouter();
  const article_id = router.query.id;

  if (article_id == null)
    return null;


  const { data, loading } = useQuery(articleQuery, {
    variables: { id: article_id },
  })

  if (loading)
    return <Loading/>
  let hero = {
    background: data.articlePage.hero.background,
    title: `${data.article.header.title}`,
    subTitle: "",
    text:`${data.article.header.title}`,
  };

  return (
    <>
      <Banner hero={hero} children={null} />
      <Article article={data.article} featured={data.featured} />
    </>
  )

}
