import React from 'react';
import Landing from './landing';
import Banner from "../components/hero/landing-hero";

import { gql, useQuery } from '@apollo/client'

const homepageQuery = gql`
# Write your query or mutation here
query {
# Write your query or mutation here
query {
  homePage {
    hero{
      title
      subTitle
      background{
        url
      }
    }
    serviceSectionTitle
    serviceSectionSubTitle
    testimonySectionTitle
    testimonySectionSubTitle
    fleetSectionTitle
    fleetSectionSubTitle
    articleSectionTitle
    articleSectionSubTitle

  }
  services(sort: "created_at" ){
    id
    name
    shortDescription{
      content
    }
    longDescription{
      content
    }

  }

    articles(sort: "created_at" ){
    id
    title
    subTitle
    body {
      ... on ComponentTextMedia{
        caption
        media {
          url
        }
        meta
        __typename
      }
      ... on ComponentTextParagraph{
        content
        __typename
      }
    }
  }
}
`


export default function index() {
  const { data, loading } = useQuery(homepageQuery)

  if (loading)
    return <Loading/>
  return <Landing data={data} />;
}
