import React, { FunctionComponent } from "react";
import styled from "styled-components";

const Input = styled('input')`
width: 100%;
padding: 12px 0px;
margin-bottom: 40px;
display: inline-block;
border: 2px solid #ccc;
border-radius: 4px;
box-sizing: border-box;
margin-top:10px;
font-size:16px;
&& {
  :focus {
    outline: none;
    border: 2px solid #A02167;
  }
  padding-left: 25px;
  letter-spacing: 25px; 
  background-image: linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%),
  linear-gradient(to left, #ccc 100%, rgba(255, 255, 255, 0) 0%);
  background-size: 10px 2px, 10px 2px,10px 2px,10px 2px,10px 2px,10px 2px;
    background-repeat:no-repeat;
    background-position:25px center, 50px center,75px center, 100px center, 125px center, 150px center;
`;
const LabelText = styled("label")`
  font-weight: normal;
  font-size: 16px;
  line-height: 16px;
  color: #444444;
`;

type InputProps = {
    id: string;
    label?: string;
    placeholder: string;
    type?: string;
};

const DefaultOtpInput: FunctionComponent<InputProps> = ({
    label,
    id,
    placeholder,
    type
}) => {
    return (
        <>
            <LabelText>{label}</LabelText>
            <Input maxLength="6" type={type} placeholder={placeholder} id={id} />
        </>
    );
};

export { DefaultOtpInput, Input }
