import React, { Children, useState } from "react";
import styled from "styled-components";

const ModalView = styled("div")`
display: block;
position: fixed; 
z-index: 1;
width: 100%;
height: 100%;
overflow: auto;
background-color: rgba(0,0,0,0.6);
`;

const ModalContent = styled("div")`
    background-color: #fefefe;
    padding: 15px;
    width:400px;
    position:absolute;
    display: block;
    left:50%;
    top:50%;
    -ms-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    border-radius:5px;
`;

const CloseModal = styled("span")`
    color: #aaaaaa;
    float: right;
    font-size: 28px;
    font-weight: bold;
    padding-bottom:20px;
    && {
        :focus {
            color: #000;
            text-decoration: none;
            cursor: pointer;  
        }
        :hover {
            color: #000;
            text-decoration: none;
            cursor: pointer;  
        }
`;


const Modal = props => {

    if(!props.show)
    return null;
    return (
        <>
            <ModalView>
                <ModalContent >
                    <CloseModal onClick={() =>props.onClose() }>&times;</CloseModal>
                    {props.children}
                </ModalContent>
            </ModalView>
        </>
    );
};

export default Modal;
