import React from 'react';
import LandingContent from '../components/landing';
import Banner from "../components/hero/landing-hero";
import Loading from "../components/loading/";

import { gql, useQuery } from '@apollo/client'

const homePageQuery = gql`
# Write your query or mutation here
query {
  homePage {
    hero{
      title
      subTitle
      background{
        url
      }
    }
    serviceSectionTitle
    serviceSectionSubTitle
    testimonySectionTitle
    testimonySectionSubTitle
    fleetSectionTitle
    fleetSectionSubTitle
    articleSectionTitle
    articleSectionSubTitle

  }
  services(sort: "created_at" ){
    id
    name
    shortDescription{
      content
    }
    longDescription{
      content
    }
    thumbnail{
      url
    }

  }

    articles(sort: "created_at" ){
    id
    published_at
    featured
    header{
    	title
    	subTitle
    	thumbnail{
      	url
    	}

    }
  }
  testimonies(sort: "created_at" ){
  	id
    profileImage{
      url
    }
  	fullName
    position
    testimony
    rating
  }
    fleets(sort: "created_at" ){
  	id
    name
    description
    speed
  	image{
      url
    }
  }
}
`


export default function index() {
  const { data, loading } = useQuery(homePageQuery)

  return (
    <>
      <Banner hero={data.homePage.hero} />
      {
        loading ?
          <Loading /> :
          <LandingContent data={data} />
      }
    </>
  )
}
