import { Box, Grid } from "@material-ui/core";
import React from "react";
import styled, { useTheme } from "styled-components";
import Body from "../../Typography/Body";
import Caption from "../../Typography/Caption";
import Title from "../../Typography/Title";

function FeaturedWork({ setPadding, title, image, type, description, ...props }) {
    const theme = useTheme();

    return (
        <>
            <Grid>
                <Grid container justify="start" alignItems="center" item xs={12} sm={12} md={6}>
                    <Grid item xs={9}>
                        <Box pl={setPadding ? 4 : 0}>
                            <Box pb={1} pt={1}>
                                <Caption color={theme.colors.primary}>
                                    {type}
                                </Caption>
                            </Box>
                            <Box pb={1} pt={1}>
                                <Title>{title}</Title>
                            </Box>

                            <Box pb={1} pt={1}>
                                <Body>{description}</Body>
                            </Box>
                        </Box>
                    </Grid>
                </Grid >
                <Grid item xs={12} sm={12} md={6}>
                    <ImageBlock src={image} />
                </Grid>
            </Grid>

        </>
    );
}


const ImageBlock = styled.img`
  width: 100%;
  overflow: hidden;
`;

export default FeaturedWork;
