import { Box, Grid } from "@material-ui/core";
import React from "react";
import styled, { useTheme } from "styled-components";
import Body from "../../Typography/Body";
import Title from "../../Typography/Title";

function FeaturedWork({ title, image, type, description, ...props }) {
    const theme = useTheme();


    return (
        <Wrapper>
        <Grid container item>
            <Grid xs={12} sm={12} md={6}>
                <Image imageSrc={image} />
            </Grid>
            <Grid direction="row" xs={12} sm={12} md={6}>
                <Grid>
                    <Title sm color={theme.colors.primary}>
                        {type}
                    </Title>
                </Grid>
                <Grid>
                    <Box clone pb={3} pt={3}>
                        <Body fontWeight="800">{title}</Body>
                    </Box>
                </Grid>
                <Grid>
                    <Box clone pb={3} pt={1}>
                        <Body>{description}</Body>
                    </Box>
                </Grid>

            </Grid>

        </Grid>
        </Wrapper>
    );
}
const Wrapper = styled.div`


`;

const Image = styled.div`
  background: radial-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 1))
    ${(props) => `, url(${props.imageSrc})`};
  background-size: cover;
  background-position: center;
  width: -webkit-fill-available;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
  height: 420px;
`;


export default FeaturedWork;
