import React, { useState } from "react";
import { Container, Grid } from "@material-ui/core";
import Content from "./Content";
import Button from "../../Button";
import styled from "styled-components";



function OurWorks() {
    const [featured] = useState([
        {
            title: "KEMIS YELEBESKU LET",
            image: "assets/kemis-yelebesku.png",
            type: "Feature Film",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "TEDDY AFRO - MAR ESKE TUAF",
            image: "assets/teddy-afro.png",
            type: "Music Video",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "EWUR AMORA KELABI",
            image: "assets/ewuramora.png",
            type: "Documentary",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "JAON BEER",
            image: "assets/jano.png",
            type: "Advertising",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
    ]);

    return (
        <>
            <Container>
                {featured.map((f, key) => (
                    <Grid direction={key % 2 != 0 ? "" : "row-reverse"} container>
                        <Content setPadding={key % 2 == 0} {...f} />
                    </Grid>
                ))}

            </Container>
            <ButtonContainer>
                <Button>View More</Button>
            </ButtonContainer>
        </>
    );
}

export default OurWorks;


const ButtonContainer = styled(Container)`
margin-top:40px;
text-align:center;
`;
