import React from "react";
import styled from "styled-components";
import { color, space, typography } from "styled-system";

function Typography({ children, as = "span", ...other }) {
  return (
    <Component as={as} {...other}>
      {children}
    </Component>
  );
}

const Component = styled.div`
  color: white;
  display: inline-block;
  ${(prop) => prop.allCaps && "text-transform: uppercase;"}
  ${typography}
  ${color}
  ${space} /* ${(prop) => prop.center && "text-align: center;"} */
  /* ${(prop) => prop.align && `text-align: ${prop.align};`} */
  /* ${(prop) => prop.bGap && `padding-bottom: ${prop.bGap}`} */
`;

export default Typography;
