import { Box } from "@material-ui/core";
import React from "react";
import styled, { useTheme } from "styled-components";
import Body from "../Typography/Body";
import Title from "../Typography/Title";

function FeaturedWork({ title, image, type, ...props }) {
    const theme = useTheme();
    const [ourWorks] = useState([
        {
          title: "KEMIS YELEBESKU LET",
          image: "assets/kemis-yelebesku.png",
          type: "Feature Film",
        },
        {
          title: "TEDDY AFRO - MAR ESKE TUAF",
          image: "assets/teddy-afro.png",
          type: "Music Video",
        },
        {
          title: "EWUR AMORA KELABI",
          image: "assets/ewuramora.png",
          type: "Documentary",
        },
        {
          title: "JAON BEER",
          image: "assets/jano.png",
          type: "Advertising",
        },
      ]);

    return (
        <MainWrapper>
            <Image imageSrc={image} />

            <Title sm color={theme.colors.primary}>
                {title}
            </Title>
            <Box clone pb={3} pt={1}>
                <Body fontWeight="700">{type}</Body>
            </Box>

        </MainWrapper>
    );
}

const MainWrapper = styled.div`
  background-size: cover;
  background-position: center;

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
`;
const Image = styled.div`
  background: radial-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 1))
    ${(props) => `, url(${props.imageSrc})`};

  background-size: cover;
  background-position: center;

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-end;
  height: 420px;
`;


export default FeaturedWork;
