import React from "react";
import styled, { useTheme } from "styled-components";
import { Link, useHistory, useRouteMatch } from "react-router-dom";
import { FlexWrapper } from "../Wrappers/FlexWrapper";
import Typography from "../Typography";

function NavigationLinks() {
  // added to force a rerender of the component
  useRouteMatch("/");
  const history = useHistory();
  const path = history.location.pathname;
  const theme = useTheme();
  return (
    <FlexWrapper>
      <NavLink to="/portfolio" active={path === "/portfolio"}>
        <Typography color={path === "/portfolio"?theme.colors.primary:null}> Portfolio</Typography>
      </NavLink>
      <NavLink to="/upcoming" active={path === "/upcoming"}>
      <Typography color={path === "/upcoming"?theme.colors.primary:null}> Upcoming</Typography>
      </NavLink>
      <NavLink to="/services" active={path === "/services"}>
      <Typography color={path === "/services"?theme.colors.primary:null}>Services</Typography>
      </NavLink>

      <NavLink to="/about-us" active={path === "/about-us"}>
      <Typography color={path === "/about-us"?theme.colors.primary:null}>About us</Typography>
      </NavLink>
      <NavLink to="/contact-us" active={path === "/contact-us"}>
      <Typography color={path === "/contact-us"?theme.colors.primary:null}> Contact us</Typography>
      </NavLink>
    </FlexWrapper>
  );
}

const NavLink = styled(Link)`
  padding: 12px 20px;
  text-decoration: none;
  border-bottom-width: 2px;
  border-bottom-style: solid;
  border-bottom-color: transparent;
  transition: border 0.4s ease;
  text-shadow: 0px 0px 2px rgba(0, 0, 0, 1);

  ${(props) =>
    props.active
      ? `
  /*border-bottom-color: ${props.theme.colors.primary};*/
  border-bottom-width: 4px;
    `
      : `
  &:hover {
    border-bottom-color: #fff;
  }
  `}
`;

export default NavigationLinks;
