import React from "react";
import styled from "styled-components";
import Typography from "../Typography";

function Hero({ heading, caption, image }) {
    console.log(image);
  return (
    <MainWrapper bgImage={image}>
      <ContentWrapper>
        <Typography allCaps textAlign="center" as="h1">
          {heading}
        </Typography>
        <Typography textAlign="center" as="h3">
          {caption}
        </Typography>
      </ContentWrapper>
    </MainWrapper>
  );
}

const MainWrapper = styled.div`
  background: radial-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 1))
    ${(props) => `, url(${props.bgImage})`};
  background-size: cover;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 80vh;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export default Hero;
