import { Box, Grid } from "@material-ui/core";
import React, {useState} from "react";
import Title from "../../Typography/Title";

function InfoCount() {
    const [infosCount] = useState([
        { count: 100, title: "BRAND VIDEOS" },
        { count: 50, title: "CREATIVE CONTENT" },
        { count: 30, title: "HAPPY USERS" },
        { count: 5, title: "AWARDS WON" },
      ]);
    return (
        <>
            <Grid container item>
                {infosCount.map((item, key) => (
                    <Grid
                        key={key}
                        container
                        item
                        xs={12}
                        sm={6}
                        md={3}
                        direction="column"
                        alignItems="center"
                        spacing={1}
                    >
                        <Grid item>
                            <Title>{item.count}+</Title>
                        </Grid>
                        <Grid item>
                            <Title sm>{item.title}</Title>
                        </Grid>
                        <Box height="20px" />
                    </Grid>
                ))}
            </Grid>
        </>
    );
}

export default InfoCount;
