import React, { useState } from "react";
import { Container, Grid, Box } from "@material-ui/core";
import styled from "styled-components";
import Title from "../Typography/Title";
import Body from "../Typography/Body";
import Caption from "../Typography/Caption";
import Image from "../Images";



const gap = "100px";
function Services() {
    const [services] = useState([
        {
            title: "KEMIS YELEBESKU LET",
            image: "assets/kemis-yelebesku.png",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "TEDDY AFRO - MAR ESKE TUAF",
            image: "assets/teddy-afro.png",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "EWUR AMORA KELABI",
            image: "assets/ewuramora.png",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "JAON BEER",
            image: "assets/jano.png",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
    ]);

    return (
        <>
        <Container>
            <Grid container direction="column" alignItems="center" spacing={3}>
                <Grid item>
                    <Caption>What we do</Caption>
                </Grid>
                <Grid item>
                    <Title>Our Services</Title>
                </Grid>
                <Box pt={3} pb={3}>
                <Grid item>
                    {services.map((s, key) => (
                        <Grid direction={key % 2 != 0 ? "row" : "row-reverse"} justify="space-between" key={key} container spacing={5}>
                            <Grid item xs={12} sm={12} md={8}>
                                <Box pb={2}>
                                    <Title>{s.title}</Title>
                                </Box>
                                <Box>
                                    <Body>{s.description}</Body>
                                </Box>
                            </Grid>

                            <Grid item xs={12} sm={12} md={3}>
                                <Image src={s.image} />
                            </Grid>
                        </Grid>
                    ))}
                </Grid>
                </Box>
            </Grid>
            </Container>

        </>
    );
}

const GridView = styled(Grid)`
&:after {
    content: "";
    height: 2px;
    width: 100px;
    background-color: #A02167;
    position: absolute;
    bottom: -.2em;
    left: 50%;
    transform: translate(-50%);
  }`;

export default Services;
