import styled from "styled-components";
import Typography from ".";

const Title = styled(Typography)`
  font-size: ${(prop) => (prop.sm ? "20px" : "32px")};
  text-transform: uppercase;
  font-weight: ${(prop) => (prop.sm ? 700 : 900)};
`;

export default Title;
