import React, { useState } from "react";
import styled from "styled-components";
import {
  FilmProductionIcon,
  MusicVideosIcon,
  DocumentariesIcon,
  CompanyProfilesIcon,
  TvShowsIcon,
  AdvertisingIcon,
} from "../Icons/MovieIcons";
import { Box, Grid, Container } from "@material-ui/core";
import Body from "../Typography/Body";
import Caption from "../Typography/Caption";
import Title from "../Typography/Title";

function WhatWeDo() {
  const [services] = useState([
    {
      icon: <FilmProductionIcon />,
      title: "FILM PRODUCTION",
      shortDesc:
        "Lorem ipsum dolor sit, amet consectetur adipiscing elit, sed do eiusmod tempor incididunt labore dolore magna.",
    },
    {
      icon: <MusicVideosIcon />,
      title: "MUSIC VIDEOS",
      shortDesc:
        "Lorem ipsum dolor sit, amet consectetur adipiscing elit, sed do eiusmod tempor incididunt labore dolore magna.",
    },
    {
      icon: <DocumentariesIcon />,
      title: "DOCUMENTARIES",
      shortDesc:
        "Lorem ipsum dolor sit, amet consectetur adipiscing elit, sed do eiusmod tempor incididunt labore dolore magna.",
    },
    {
      icon: <CompanyProfilesIcon />,
      title: "COMPANY PROFILES",
      shortDesc:
        "Lorem ipsum dolor sit, amet consectetur adipiscing elit, sed do eiusmod tempor incididunt labore dolore magna.",
    },
    {
      icon: <TvShowsIcon />,
      title: "TV SHOWS",
      shortDesc:
        "Lorem ipsum dolor sit, amet consectetur adipiscing elit, sed do eiusmod tempor incididunt labore dolore magna.",
    },
    {
      icon: <AdvertisingIcon />,
      title: "ADVERTISING",
      shortDesc:
        "Lorem ipsum dolor sit, amet consectetur adipiscing elit, sed do eiusmod tempor incididunt labore dolore magna.",
    },
  ]);
  return (
    <Container>
      <SectionWrapper as="section">
        <Box
          display="flex"
          flexDirection="column"
          alignItems="center"
          paddingBottom={4}
          gridGap={20}
        >
          <Caption>What We Do</Caption>
          <Title>OUR SERVICES</Title>
        </Box>
        <Box padding={1.5}>
          <ServicesGrid container spacing={3}>
            {services.map((item, key) => (
              <ServicesItem key={key} item xs={12} sm={6} md={4} lg={4}>
                <Grid
                  container
                  alignItems="center"
                  direction="column"
                  spacing={2}
                >
                  <Grid item>
                    <Icon>{item.icon}</Icon>
                  </Grid>
                  <Grid item>
                    <Title sm>{item.title}</Title>
                  </Grid>
                  <Grid item>
                    <Body textAlign="center">{item.shortDesc}</Body>
                  </Grid>
                </Grid>
              </ServicesItem>
            ))}
          </ServicesGrid>
        </Box>
      </SectionWrapper>
    </Container>
  );
}

const SectionWrapper = styled(Box)`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ServicesGrid = styled(Grid)``;

const ServicesItem = styled(Grid)`
  align-items: center;
`;

const Icon = styled(Box)``;

export default WhatWeDo;
