import React, { useState } from "react";
import { Container, Grid, Box, makeStyles } from "@material-ui/core";
import Content from "./Content";


const gap = "100px";
function Services() {
    const classes = useStyles();

    const [featured] = useState([
        {
            title: "KEMIS YELEBESKU LET",
            image: "assets/kemis-yelebesku.png",
            type: "Feature Film",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "TEDDY AFRO - MAR ESKE TUAF",
            image: "assets/teddy-afro.png",
            type: "Music Video",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "EWUR AMORA KELABI",
            image: "assets/ewuramora.png",
            type: "Documentary",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
        {
            title: "JAON BEER",
            image: "assets/jano.png",
            type: "Advertising",
            description: "It’s a one step hub and leading company for film production and Television commercial works in Ethiopia. A creative hot bed where exchange of ideas forms the genesis of our work."
        },
    ]);

    return (
        <>
            <Container>
                {featured.map((f, key) => (
                    <div>
                        <Grid className={classes.root} direction={key % 2 != 0 ? "row" : "row-reverse"} container spacing={4}>
                            <Content  {...f} />
                            {useState}
                        </Grid>
                        <Box height={gap} />
                    </div>
                ))}

            </Container>

        </>
    );
}

const useStyles = makeStyles((theme) => ({
    root: {
        "&::after": {
            display: "block",
            content: '""',
            border: "1px dashed white",
            width: 120,
            left: "50%",
            margin: "40px auto",
        }
    },
}));

export default Services;
