import { Hidden } from "@material-ui/core";
import React from "react";
import { Link } from "react-router-dom";
import styled, { useTheme } from "styled-components";
import Logo from "./Logo";
import NavigationLinks from "./NavigationLinks";
import NavigatoinActions from "./NavigatoinActions";


function Header() {
let theme = useTheme();
  return (
    <Floater>
      <Wrapper theme={theme}>
        <Link to="/">
          <Logo />
        </Link>
        <Hidden smDown>
          <NavigationLinks />
        </Hidden>
        <NavigatoinActions />
      </Wrapper>
    </Floater>
  );
}

const Wrapper = styled.section`
  box-sizing: border-box;
  padding: 0 30px;
  height: 70;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  background-color: ${props.theme.colors.primary}; 
`;

const Floater = styled.section`
  box-sizing: border-box;
  position: fixed;
  width: 100%;
  z-index: 1;
`;

export { Header };
