import { Container, Grid } from "@material-ui/core";
import React from "react";
import styled from "styled-components";
import Body from "../Typography/Body";
import Caption from "../Typography/Caption";
import Title from "../Typography/Title";

function WhoWeAre() {
  return (
    <Container>
      <SectionWrapper>
        <Grid container spacing={8}>
          <Grid item xs={12} md={6}>
            <Caption pb="12px">Who We are</Caption>
            <Title pb="24px">We are a creative video production company</Title>
            <Body pb="24px">
              The Story Of Sewmehon Films began on 2012. Passionate storyteller
              friends from different backgrounds established it.
            </Body>
            <Body pb="24px">
              It’s a one step hub and leading company for film production and
              Television commercial works in Ethiopia. A creative hot bed where
              exchange of ideas forms the genesis of our work.
            </Body>
            <Body pb="24px">
              Be it a future film, advertising, animation, corporate videos or
              company profiles and documentaries, we have the expertise to
              create beautiful images for each one of the mediums.
            </Body>
            <Caption pb="12px">Who We are</Caption>
            <Body pb="24px">
              The Story Of Sewmehon Films began on 2012. Passionate storyteller
              friends from different backgrounds established it.
            </Body>
            <Body pb="24px">
              It’s a one step hub and leading company for film production and
              Television commercial works in Ethiopia. A creative hot bed where
              exchange of ideas forms the genesis of our work.
            </Body>
            <Body pb="24px">
              Be it a future film, advertising, animation, corporate videos or
              company profiles and documentaries, we have the expertise to
              create beautiful images for each one of the mediums.
            </Body>
          </Grid>
          <Grid item xs={12} md={6}>
            <ImageBlock src="/assets/production.png" />
          </Grid>
        </Grid>
      </SectionWrapper>
    </Container>
  );
}

const SectionWrapper = styled.section`
  display: flex;
  flex-direction: row;
  gap: 20px;
  padding: 16px;
`;

const ImageBlock = styled.img`
  border-radius: 8px;
  width: 100%;
  overflow: hidden;
`;

export default WhoWeAre;
